package day19import kotlin.test.*internal class ReadingTest {    @Test    fun `reading should generate all orientations`() {        val x = 5        val y = 6        val z = -4        val reading = Reading(Pos(5, 6, -4))        val allOrientations = reading.orientations()        assertEquals(            setOf(                Pos(x, y, z),                Pos(x, -z, y),                Pos(x, -y, -z),                Pos(x, z, -y),                Pos(z, y, -x),                Pos(-x, y, -z),                Pos(-z, y, x),                Pos(-y, x, z),                Pos(-x, -y, z),                Pos(-y, -x, z),            ), allOrientations.map { it.pos }.toSet()        )    }    @Test    internal fun `scanner 0 should align to scanner 1`() {        assertTrue(scanner0.alignsTo(scanner1) != null)    }    @Test    fun `scanner 4 should align to scanner 1`() {        val alignedScanner1 = scanner0.alignsTo(scanner1)!!        println(alignedScanner1)        assertTrue(alignedScanner1.alignsTo(scanner4) != null)    }    val scanner0 = Scanner(        setOf(            Reading(Pos(404, -588, -901)),            Reading(Pos(528, -643, 409)),            Reading(Pos(-838, 591, 734)),            Reading(Pos(390, -675, -793)),            Reading(Pos(-537, -823, -458)),            Reading(Pos(-485, -357, 347)),            Reading(Pos(-345, -311, 381)),            Reading(Pos(-661, -816, -575)),            Reading(Pos(-876, 649, 763)),            Reading(Pos(-618, -824, -621)),            Reading(Pos(553, 345, -567)),            Reading(Pos(474, 580, 667)),            Reading(Pos(-447, -329, 318)),            Reading(Pos(-584, 868, -557)),            Reading(Pos(544, -627, -890)),            Reading(Pos(564, 392, -477)),            Reading(Pos(455, 729, 728)),            Reading(Pos(-892, 524, 684)),            Reading(Pos(-689, 845, -530)),            Reading(Pos(423, -701, 434)),            Reading(Pos(7, -33, -71)),